package com.javaprograms;

public class WhileDoWhileExamples {

	public static void main(String[] args) {
		
		int i=0;
		while(i<5)
		{
			System.out.println("value of i is "+i);
			i++;
			
		}
		// TODO Auto-generated method stub
		int j=0;
		do
		{
			System.out.println("value of j is "+j);
			j++;
		}while(j<10);

	}

}
